import React, { useState } from 'react'
import { plantList } from '../datas/plantList'
import PlantItem from './PlantItem'
import '../styles/ShoppingList.css'
import Categories from './Categories'



function ShoppingList({cart, updateCart}) {
    const [activeCategory, setActiveCategory] = useState('')

	const reducer = (acc, plant) => acc.includes(plant.category) ? acc : acc.concat(plant.category)

    const categories = plantList.reduce(reducer, [])

    function addToCart(name, price) {
        const currentPlantAdded = cart.find((plant) => plant.name === name)
        if (currentPlantAdded) {
            const cartFilteredCurrentPlant = cart.filter((plant) => plant.name !== name
            )
        updateCart([...cartFilteredCurrentPlant, { name, price, amount: currentPlantAdded.amount + 1}
        ])
        } else {
            updateCart([...cart, { name, price, amount: 1}])
        }
    }

    return (
        <div className='lmj-shopping-list'>
            <Categories 
                categories={categories}
                 />

            <ul className ='lmj-plant-list'>
                {plantList.map(({id, cover, name, water, light, price}) => (
                    <div key={id}>
                        <PlantItem 
                            name={name} 
                            cover={cover} 
                            id={id} 
                            light={light} 
                            water={water} 
                        />
                        <button onClick={() => addToCart(name, price)}>Ajouter</button>
                    </div>
                ))}
            </ul>
        </div>
    )
}

export default ShoppingList
