import React from 'react'
import { plantList } from '../datas/plantList'
import PlantItem from './PlantItem'
import '../styles/ShoppingList.css'



function ShoppingList({cart, addToCart}) {
	const reducer = (acc, plant) => acc.includes(plant.category) ? acc : acc.concat(plant.category)

    const categories = plantList.reduce(reducer, [])

    function addToCart(name, price) {
        const currentPlantAdded = cart.find((plant) => plant.name === name)

    }

    return (
        <div className='lmj-shopping-list'>
            <ul>
                {categories.map(cat => <li key={cat}>{cat}</li>)}
            </ul>
            <ul className ='lmj-plant-list'>
                {plantList.map(({id, cover, name, water, light}) => (
                    <div key={id}>
                        <PlantItem 
                            name={name} 
                            cover={cover} 
                            id={id} 
                            light={light} 
                            water={water} 
                        />
                        <button onClick={() => addToCart((name, price)) && console.log(cart)}>Ajouter</button>
                    </div>
                ))}
            </ul>
        </div>
    )
}

export default ShoppingList
